import React, {Component} from 'react';
import {BrowserRouter, Route} from 'react-router-dom';
import './App.css';
import './swipe.css';
import * as landing from './landing';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // hashValue: "hash",
            // name: ""
        };
    }


    render() {
        return (
            <BrowserRouter>
                <div>
                    {/*Landing Page*/}
                    <Route exact={true} path='/' component = {landing}/>

                     {/*Todo Page to redirect to with name and hashvalue*/}
                    {/*Swipe page to choose restaurants*/}
                    <Route exact={true} path='/swipe' render={() => (
                        <div className="wrapper">
                            <div className="profile-card js-profile-card">
                                <div className="profile-card__img">
                                    <img
                                        src="https://www.savt.ca/scripts/templates/iDea/images/blog-1.jpg"
                                        alt="profile card"/>
                                </div>
                                <div
                                    className="profile-card__cnt js-profile-cnt">
                                    <div className="profile-card__name">
                                        Name of place...
                                    </div>
                                    <div className="profile-card__txt">
                                        Place description
                                    </div>
                                    <div className="profile-card-loc">
                                        <span className="profile-card-loc__txt">
                                        Istanbul, Turkey
                                         </span>
                                    </div>
                                    <div className="profile-card-ctr">
                                        <button
                                            className="profile-card__button button--blue js-message-btn">
                                            Yes!
                                        </button>
                                        <button
                                            className="profile-card__button button--orange">Hell
                                            Nah!
                                        </button>
                                    </div>
                                </div>
                                <div
                                    className="profile-card-message js-message">
                                    <form className="profile-card-form">
                                        <div
                                            className="profile-card-form__container">
                                            <textarea
                                                placeholder="Say something..."/>
                                        </div>

                                        <div
                                            className="profile-card-form__bottom">
                                            <button
                                                className="profile-card__button button--blue js-message-close">
                                                Send
                                            </button>
                                            <button
                                                className="profile-card__button button--gray js-message-close">
                                                Cancel
                                            </button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    )}/>

                    {/*Todo Page to redirect to with hashvalue*/}
                    {/*Enter name page*/}
                    <Route exact={true} path='/name' render={() => (
                        <div>
                            <div className='navbar'>
                                <div className='logo'>Where to Eat ?</div>
                                <div className='seal'/>
                            </div>
                            <div className={"buttons"}>
                                <form>
                                    <label className={"label-name"}>Enter you
                                        Name:</label>
                                    <input className={"input-name"} type="text"
                                           placeholder={"Name:"} id="nameBox"/>
                                    <input className={"button-submit"}
                                           type="button" value="Submit"
                                           onClick={storeName.bind(this)}/>
                                </form>
                            </div>
                        </div>
                    )}/>
                </div>
            </BrowserRouter>
        );

        function enterCode() {
            var create = document.getElementById("createButton");
            var enterCode = document.getElementById("enterCode")
            var codeTextBox = document.createElement('input');
            var cancel = document.getElementById("cancelButton");
            cancel.style.visibility = "visible";
            codeTextBox.classList.add("inputText");
            codeTextBox.placeholder = "Code:";
            codeTextBox.id = "codeTextBox";
            codeTextBox.background = "black";
            create.classList.add("change");
            create.value = "";
            //add delay to changing classes
            setTimeout(function () {
                create.value = "Code:";
                enterCode.innerText = "Submit";
                create.classList.add("change2");
            }, (0.5 * 1000));
            setTimeout(function () {
                create.parentNode.replaceChild(codeTextBox, create);
            }, (1 * 1000));
            // makes button redirect to name page on click
            enterCode.onclick = redirectToName;

        }

        function returnCode() {
            //TODO return code for clicking cross
            //code text box to replace
            var inputBox = document.getElementById("codeTextBox");
            //second button
            var enterCode = document.getElementById("enterCode")
            //create form button
            var createButton = document.createElement('input');
            //cancel button
            var cancel = document.getElementById("cancelButton");
            //change cancel button to hidden
            cancel.style.visibility = "hidden";
            //add button class to createButton
            createButton.classList.add("button-return");
            //Add text to createButton
            createButton.value = "Generate Code";
            //Add id to createButton
            createButton.id = "createButton";
            createButton.type = "button";
            createButton.onclick = getLocation.bind(this);
            inputBox.parentNode.replaceChild(createButton, inputBox);
            createButton.classList.add("change");

            setTimeout(function () {
                createButton.classList.add("change2");
                enterCode.innerText = "Enter code";
                enterCode.id = "enterCode"
            }, (0.5 * 1000));
        }

        function getLocation() {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(showPosition.bind(this));
                var createButton = document.createElement('button');
                createButton.onclick = showHash.bind(this);
                setTimeout(function () {
                    createButton.click();
                }, (0.5 * 1000));
            } else {
                alert("Geolocation is not supported by this browser.");
            }
        }

        function showPosition(position) {
            fetch('http://localhost:8080/getCode?latitude=' + position.coords.latitude + '&longitude=' + position.coords.longitude, {
                method: 'get'

            }).then(res => {
                if (!res.ok) {
                    throw res;
                }
                return res;
            }).then(response => response.json())
                .then(response => {
                    this.setState({hashValue: response});
                    // showHash(response);
                })
        }

        function showHash() {
            //code text box to replace
            var enterCode = document.getElementById("enterCode")
            //create form button
            var copyButton = document.createElement('button');
            //add button class to createButton
            copyButton.classList.add("button-return");
            //Add text to createButton
            copyButton.innerHTML = "";
            //Add id to createButton
            copyButton.id = "copyButton";
            //To copy Text on clicking
            copyButton.onclick = copyFunction;
            //Replace child
            enterCode.parentNode.replaceChild(copyButton, enterCode);
            //Add animations
            copyButton.classList.add("change");
            var hashVal = "" + this.state.hashValue;
            setTimeout(function () {
                copyButton.classList.add("change2");
            }, (0.5 * 1000));
            setTimeout(function () {
                copyButton.innerHTML = hashVal;
            }, (0.8 * 1000));
            document.getElementById("copyCodeText").style.visibility = "visible";
            document.getElementById("createButton").value = "Restaurant picker";
            // Redirect to name page
            document.getElementById("createButton").onclick = redirectToName;
        }

        function redirectToName() {
            // Todo sid redirect: send hashvalue as param
            window.location.href = "http://localhost:3000/name";
        }

        function copyFunction() {
            console.log("reached");
            var toChange = document.getElementById("copyButton");
            var textarea = document.createElement("textarea");
            textarea.value = toChange.innerHTML;
            document.body.appendChild(textarea);
            textarea.select();
            document.execCommand('copy');
            document.body.removeChild(textarea);
        }

        function storeName() {
            var name = document.getElementById("nameBox").value;
            this.setState({name: name});
            if (this.state.name !== "")
                // Todo sid redirect: send name and hashvalue as param
                window.location.href = "http://localhost:3000/swipe";
                //console.log(this.state.hashValue);
            else
                alert("Please enter a name")
        }

    }
}

export default App;
